    private Validator() {
    }

    private static ValidatorFactory factory = Validation.buildDefaultValidatorFactory();

    public static <T> Map<String, String> validate(T object) {
        final HashMap<String, String> errors = new HashMap<>();
        hibernateValidate(object, errors);
        customValidate(object, errors);
        return errors;
    }

    private static <T> void hibernateValidate(T object, Map<String, String> errorMap) {
        final javax.validation.Validator validator = factory.getValidator();
        final Set<ConstraintViolation<T>> constraintViolations = validator.validate(object);
        for (ConstraintViolation<T> violation : constraintViolations) {
            errorMap.put(violation.getPropertyPath().toString(), violation.getMessage());
        }
    }

    private static <T> void customValidate(T object, Map<String, String> errorMap) {
        List<Field> fieldList = new ArrayList<>();
        Class<?> objectClass = object.getClass();
        while (!objectClass.equals(Object.class)) {
            fieldList.addAll(Arrays.asList(objectClass.getDeclaredFields()));
            objectClass = objectClass.getSuperclass();
        }
        getNotInFutureLocalDateComplianceErrors(object, fieldList, errorMap);
        getPastYearsRestrictionComplianceErrors(object, fieldList, errorMap);

    }

    private static <T> void getNotInFutureLocalDateComplianceErrors(T object, List<Field> fieldList,
                                                                    Map<String, String> errorMap) {
        final Class<NotInFutureLocalDate> annotationClass = NotInFutureLocalDate.class;
        for (Field field : fieldList) {
            if (errorMap.containsKey(field.getName())) {
                continue;
            }
            if (field.isAnnotationPresent(annotationClass)) {

                if (!field.getType().equals(LocalDate.class)) {
                    log.error("{} annotation should be only on LocalDate field", annotationClass);
                    throw new IllegalAnnotationOnField(annotationClass.getSimpleName() +
                            "annotation should be on LocalDate field");
                }
                try {
                    LocalDate birthday = (LocalDate) new PropertyDescriptor(field.getName(), object.getClass())
                            .getReadMethod().invoke(object);
                    if (birthday.isAfter(LocalDate.now())) {
                        errorMap.put(field.getName(),
                                field.getAnnotation(annotationClass).message());
                    }
                } catch (IllegalAccessException e) {
                    log.error("{} field is inaccessible", field.getName());
                    throw new ServerSideException(e);
                } catch (IntrospectionException e) {
                    log.error("{} not being able to map a string class name  to a Class object", object.getClass());
                    throw new ServerSideException(e);
                } catch (InvocationTargetException e) {
                    log.error("{} thrown at invoking getter method of {} field", e, field.getName());
                }
            }
        }
    }

    private static <T> void getPastYearsRestrictionComplianceErrors(T object, List<Field> fieldList, Map<String,
            String> errorMap) {
        final Class<PastYearsRestriction> annotationClass = PastYearsRestriction.class;
        for (Field field : fieldList) {
            if (errorMap.containsKey(field.getName())) {
                continue;
            }
            if (field.isAnnotationPresent(PastYearsRestriction.class)) {

                if (!field.getType().equals(LocalDate.class)) {
                    log.error("{} annotation should be only on LocalDate field", annotationClass);
                    throw new IllegalAnnotationOnField(annotationClass.getSimpleName() +
                            "annotation should be only on an LocalDate field");
                }

                try {
                    LocalDate birthday = (LocalDate) new PropertyDescriptor(field.getName(), object.getClass())
                            .getReadMethod().invoke(object);
                    LocalDate maxPastDate = LocalDate.now()
                            .minusYears(field.getAnnotation(annotationClass).count());
                    if (birthday.isBefore(maxPastDate)) {
                        errorMap.put(field.getName(), field.getAnnotation(annotationClass).message());
                    }

                } catch (IllegalAccessException e) {
                    log.error("{} field is inaccessible", field.getName());
                    throw new ServerSideException(e);
                } catch (IntrospectionException e) {
                    log.error("{} not being able to map a string class name  to a Class object", object.getClass());
                    throw new ServerSideException(e);
                } catch (InvocationTargetException e) {
                    log.error("{} thrown at invoking getter method of {} field", e, field.getName());
                }
            }
        }
    }
